  static bool checkForUUID(PyObject* pyObj)
  {
    std::string tmp(PyString_AsString(PyObject_Str(pyObj)));
    return tmp.find("SMTK") == std::string::npos && tmp.find("-")==8 &&
           tmp.find("-", 9) == 13 && tmp.find("-", 14) == 18 &&
           tmp.find("-", 19) == 23 && tmp.find("-", 24) == std::string::npos;
  }






